// Calculate settlement authority based on loan history
const balance = $input.item.json.current_balance || 0;
const daysPastDue = $input.item.json.days_past_due || 0;
const totalLoans = $input.item.json.total_loans || 0;
const avgDaysLate = $input.item.json.avg_days_late || 0;
const channelPref = $input.item.json.channel_preference || 'email';

let baseDiscount = 0.10; // Start at 10%

// Time-based escalation
if (daysPastDue > 30) baseDiscount += 0.05;
if (daysPastDue > 60) baseDiscount += 0.10;
if (daysPastDue > 90) baseDiscount += 0.10;

// History-based adjustment
if (avgDaysLate > 30) baseDiscount += 0.05;
if (totalLoans > 5) baseDiscount -= 0.05; // Loyalty discount reduction

// Cap at 40% maximum discount
const maxDiscount = Math.min(baseDiscount, 0.40);

// Determine personality profile
let personality = 'standard';
if (avgDaysLate > 0 && avgDaysLate <= 10) personality = 'reliable_but_slow';
else if ($input.item.json.favorite_payment_day === 15 || $input.item.json.favorite_payment_day === 30) personality = 'payday_payer';
else if ($input.item.json.partial_payments > 2) personality = 'partial_payer';
else if (avgDaysLate > 60) personality = 'chronic_late';
else if (totalLoans === 0) personality = 'ghost';

// Determine communication channel
let selectedChannel = 'email'; // Default
if (channelPref && channelPref.toLowerCase().includes('sms')) {
  selectedChannel = 'sms';
} else if (channelPref && channelPref.toLowerCase().includes('both')) {
  selectedChannel = 'both';
}

// Return in correct format for n8n Code nodes
return [{
  json: {
    ...$input.item.json,
    settlement_authority: {
      max_discount: maxDiscount,
      min_acceptable: balance * (1 - maxDiscount),
      immediate_settlement: balance * (1 - maxDiscount - 0.05),
      payment_plan_months: daysPastDue > 60 ? 6 : 3
    },
    personality_profile: personality,
    communication_channel: selectedChannel,
    follow_up_count: 0 // First outreach
  }
}];

// Parse OpenAI response and prepare messages
const aiResponse = JSON.parse($input.item.json.message.content);
const channel = $input.item.json.communication_channel;
const customerId = $input.item.json.customer_id;

// Payment links
const paymentLink = `https://pay.example.com/${customerId}`;
const arrangementLink = `https://arrange.example.com/${customerId}`;

// Email signature
const emailSignature = `<p><b>Here are a few paths forward:</b></p>
<p>âœ… <b>Make a Payment Today</b><br>
If you're ready to tackle your balance now, you can make a payment securely here: <a href="${paymentLink}">[Click Here]</a>.</p>
<p>âœ… <b>Set Up a Payment Arrangement</b><br>
If a full payment isn't possible right now, no worries. You can arrange a flexible payment plan that works for your budget: <a href="${arrangementLink}">[Click Here]</a>.</p>
<p>âœ… <b>Smart Repayment Loan Program</b><br>
Looking for a fresh start? You may qualify for our new program!</p>
<p>A prompt response will give us a chance to help you effectively. Please give me a reply as soon as you can to talk about the best solution together!<br>
<b>Jordan A.<br>Collections Department</b></p>`;

// Prepare final email with signature
const finalEmailBody = aiResponse.email_body + emailSignature;

// Add payment link to SMS if needed
const finalSmsBody = aiResponse.sms_body + ` Reply or visit: ${paymentLink}`;

// Determine what to send
let sendEmail = false;
let sendSms = false;

if (channel === 'both') {
  sendEmail = true;
  sendSms = true;
} else if (channel === 'sms') {
  sendSms = true;
} else {
  sendEmail = true; // Default to email
}

// Return in correct format
return [{
  json: {
    customer_id: $input.item.json.customer_id,
    customer_name: $input.item.json.name,
    phone: $input.item.json.phone,
    email: $input.item.json.email,
    subject: aiResponse.subject,
    email_body: finalEmailBody,
    sms_body: finalSmsBody,
    updated_summary: aiResponse.updated_summary,
    send_email: sendEmail,
    send_sms: sendSms,
    current_balance: $input.item.json.current_balance,
    settlement_authority: $input.item.json.settlement_authority,
    personality_profile: $input.item.json.personality_profile,
    communication_channel: channel
  }
}];

// Prepare conversation state for Redis
const messages = [];

// Add email if sent
if ($node["check_email"].json.send_email) {
  messages.push({
    type: 'outbound',
    channel: 'email',
    subject: $node["prepare_messages"].json.subject,
    text: $node["prepare_messages"].json.email_body,
    timestamp: new Date().toISOString()
  });
}

// Add SMS if sent
if ($node["check_sms"].json.send_sms) {
  messages.push({
    type: 'outbound',
    channel: 'sms',
    text: $node["prepare_messages"].json.sms_body,
    timestamp: new Date().toISOString()
  });
}

const conversation = {
  status: 'outreach_sent',
  messages: messages,
  offers_made: [],
  communication_channel: $node["prepare_messages"].json.communication_channel,
  last_contact: new Date().toISOString()
};

// Return in correct format
return [{
  json: {
    customer_id: $node["prepare_messages"].json.customer_id,
    conversation: conversation,
    summary: $node["prepare_messages"].json.updated_summary
  }
}];

